SELECT id, to_char(data_notificacao,'DD/MM/YYYY') AS data_notificacao, to_char(data_encerramento,'DD/MM/YYYY') AS data_encerramento, status_notificacao, origem, to_char(updated_at,'DD/MM/YYYY') AS updated_at, numero_do, classificacao_final, criterio_classificacao, evolucao, to_char(data_cura_obito,'DD/MM/YYYY') AS data_cura_obito, tipo_paciente, cns, cpf, passaporte, etnia, paciente, sexo, to_char(data_nascimento,'DD/MM/YYYY') AS data_nascimento, nome_mae, raca_cor, ocupacao, ocupacao_descricao, cod_cbo, escolaridade, pais_residencia, pais_municipio_residencia, uf_residencia, ibge_residencia, cep_residencia, logradouro_residencia, bairro_residencia, uf_unidade_notifica, ibge_unidade_notifica, to_char(data_cadastro,'DD/MM/YYYY') AS data_cadastro, to_char(data_coleta,'DD/MM/YYYY') AS data_coleta, to_char(data_recebimento,'DD/MM/YYYY') AS data_recebimento, to_char(data_liberacao,'DD/MM/YYYY') AS data_liberacao, requisicao, resultado, exame, metodo, lab_executor, to_char(data_1o_sintomas,'DD/MM/YYYY') AS data_1o_sintomas FROM notificacao WHERE classificacao_final = 2 AND excluir_ficha = 2 AND status_notificacao in (1,2) AND ((data_notificacao >= NOW() - INTERVAL '3 DAY') or (data_liberacao >= NOW() - INTERVAL '3 DAY') or (updated_at >= NOW() - INTERVAL '3 DAY')) ORDER BY 1 LIMIT ALL OFFSET 0